pragma solidity ^0.8.13;

import "@equilibria/root/number/types/UFixed6.sol";
import "@equilibria/root/token/types/Token18.sol";
import "@equilibria/root/attribute/interfaces/IInstance.sol";
import "@equilibria/perennial-v2/contracts/interfaces/IOracleProvider.sol";

interface IOracle is IOracleProvider, IInstance {
    // sig: 0x8852e53b
    error OracleOutOfSyncError();
    // sig: 0x0f7338e5
    error OracleOutOfOrderCommitError();
    // sig: 0xb0850572
    error OracleNotSubOracleError();
    // sig: 0xc65186ee
    error OracleNotMarketError();
    // sig: 0x337304b2
    error OracleNotBeneficiaryError();

    event OracleUpdated(IOracleProvider newProvider);
    event MarketUpdated(IMarket newMarket);
    event FeeReceived(UFixed6 settlementFee, UFixed6 oracleFee);
    event BeneficiaryUpdated(address newBeneficiary);

    /// @dev The state for a single epoch
    struct Epoch {
        /// @dev The oracle provider for this epoch
        IOracleProvider provider;

        /// @dev The last timestamp that this oracle provider is valid
        uint96 timestamp;
    }

    /// @dev The global state for oracle
    struct OracleGlobal {
        /// @dev The current epoch
        uint128 current;

        /// @dev The latest epoch
        uint128 latest;
    }

    function initialize(IOracleProvider initialProvider, string calldata name_) external;
    function register(IMarket newMarket) external;
    function update(IOracleProvider newProvider) external;
    function updateBeneficiary(address newBeneficiary) external;
    function updateName(string calldata newName) external;
    function claimFee(UFixed6 settlementFeeRequested) external;
    function market() external view returns (IMarket);
    function withdraw(Token18 token) external;
    function beneficiary() external view returns (address);
}","keccak256":"0xeb5235a836401522b4072cd103492de944833d51a1f2a84a587208dc65e12b6e","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/interfaces/IOracleFactory.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@equilibria/root/token/types/Token18.sol";
import "@equilibria/root/attribute/interfaces/IFactory.sol";
import "@equilibria/perennial-v2/contracts/interfaces/IOracleProviderFactory.sol";
import "@equilibria/perennial-v2/contracts/interfaces/IMarket.sol";
import "./IOracle.sol";
import { OracleParameter } from "../types/OracleParameter.sol";

interface IOracleFactory is IOracleProviderFactory, IFactory {
    event FactoryRegistered(IOracleProviderFactory factory);
    event CallerAuthorized(IFactory caller);

    // sig: 0xe7911099
    error OracleFactoryInvalidIdError();
    // sig: 0xe232e366
    error OracleFactoryAlreadyCreatedError();
    // sig: 0xbbfaa925
    error OracleFactoryNotRegisteredError();
    // sig: 0xfeb0e18c
    error OracleFactoryNotCreatedError();

    function factories(IOracleProviderFactory factory) external view returns (bool);
    function initialize() external;
    function parameter() external view returns (OracleParameter memory);
    function updateParameter(OracleParameter memory newParameter) external;
    function updateId(IOracleProvider oracleProvider, bytes32 id) external;
    function register(IOracleProviderFactory factory) external;
