","keccak256":"0x7f050c8d94445f50f5b3a1fee5f47441679ccec2022cb8cd8506524e7472874a","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/keeper/KeeperOracle.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@equilibria/root/attribute/Instance.sol";
import { IGasOracle } from "@equilibria/root/gas/GasOracle.sol";
import "../interfaces/IKeeperFactory.sol";
import { PriceResponse, PriceResponseStorage, PriceResponseLib } from "./types/PriceResponse.sol";

/// @title KeeperOracle
/// @notice Generic implementation of the IOracle interface for keeper-based oracles.
/// @dev One instance per price feed should be deployed. Multiple products may use the same
///      KeeperOracle instance if their payoff functions are based on the same underlying oracle.
///      This implementation only supports non-negative prices.
contract KeeperOracle is IKeeperOracle, Instance {
    using EnumerableSet for EnumerableSet.AddressSet;

    /// @dev The oracle provider authorized to call this sub oracle
    IOracle public oracle;

    /// @dev The gas oracles for pricing a commit keeper reward
    IGasOracle public immutable commitmentGasOracle;

    /// @dev The gas oracles for pricing a settle keeper reward
    IGasOracle public immutable settlementGasOracle;

    /// @dev After this amount of time has passed for a version without being committed, the version can be invalidated.
    uint256 public immutable timeout;

    /// @dev List of all requested new price oracle versions by index
    mapping(uint256 => uint256) public requests;

    /// @dev The global state of the oracle
    KeeperOracleGlobal private _global;

    /// @dev Mapping from timestamp to oracle version responses
    mapping(uint256 => PriceResponseStorage) private _responses;

    /// @dev Mapping from version and market to a set of registered accounts for settlement callback
    mapping(uint256 => EnumerableSet.AddressSet) private _localCallbacks;

    /// @notice Constructs the contract
    /// @param timeout_ The timeout for a version to be committed
    constructor(uint256 timeout_) {
        timeout = timeout_;
    }

    /// @notice Initializes the contract state
    function initialize() external initializer(1) {
        __Instance__initialize();
    }

    /// @notice Returns the global state of the oracle
    /// @return The global state of the oracle
    function global() external view returns (KeeperOracleGlobal memory) { return _global; }

    /// @notice Updates the registered oracle provider
    /// @dev The oracle provider is the only authorized caller
    /// @param newOracle The new oracle provider
    function register(IOracle newOracle) external onlyOwner {
        oracle = newOracle;
        emit OracleUpdated(newOracle);
    }

    /// @notice Returns the local oracle callback set for a version and market
    /// @param version The version to lookup
    /// @return The local oracle callback set for the version and market
    function localCallbacks(uint256 version) external view virtual returns (address[] memory) {
        return _localCallbacks[version].values();
    }

    /// @notice Returns the next requested oracle version
    /// @dev Returns 0 if no next version is requested
    /// @return The next requested oracle version
    function next() public view virtual returns (uint256) {
        return requests[_global.latestIndex + 1];
    }

    /// @notice Returns the response for a given oracle version
    /// @param timestamp The timestamp of the oracle version
    /// @return The response for the given oracle version
    function responses(uint256 timestamp) external view virtual returns (PriceResponse memory) {
        return _responses[timestamp].read();
    }

    /// @notice Records a request for a new oracle version
    /// @dev  - If no request has been made this version, a price request will be created
    ///       - If a request has been made this version, no action will be taken
    /// @param account The account to callback to
    function request(IMarket, address account) external virtual onlyOracle {
        uint256 currentTimestamp = current();

        _localCallbacks[currentTimestamp].add(account);
        emit CallbackRequested(SettlementCallback(oracle.market(), account, currentTimestamp));

        if (requests[_global.currentIndex] == currentTimestamp) return; // already requested new price

        requests[++_global.currentIndex] = currentTimestamp;
        emit OracleProviderVersionRequested(currentTimestamp, true);
    }

    /// @notice Returns the latest synced oracle version and the current oracle version
    /// @return The latest synced oracle version
    /// @return The current oracle version collecting new orders
    function status() external view virtual returns (OracleVersion memory, uint256) {
        return (latest(), current());
    }

    /// @notice Returns the latest synced oracle version
    /// @return latestVersion Latest oracle version
    function latest() public view virtual returns (OracleVersion memory latestVersion) {
        (latestVersion, ) = at(_global.latestVersion);
    }

    /// @notice Returns the current oracle version accepting new orders
    /// @return Current oracle version
    function current() public view virtual returns (uint256) {
        return IKeeperFactory(address(factory())).current();
    }

    /// @notice Returns the oracle version at version `version`
    /// @param timestamp The timestamp of which to lookup
    /// @return Oracle version at version `version`
    /// @return Oracle receipt at version `version`
    function at(uint256 timestamp) public view virtual returns (OracleVersion memory, OracleReceipt memory) {
        return (
            _responses[timestamp].read().toOracleVersion(timestamp),
            _responses[timestamp].read().toOracleReceipt(_localCallbacks[timestamp].length())
        );
    }

    /// @notice Commits the price to specified version
    /// @dev Verification of price happens in the oracle's factory
    /// @param version The oracle version to commit
    /// @param receiver The receiver of the settlement fee
    /// @param value The value charged to commit the price in ether
    function commit(OracleVersion memory version, address receiver, uint256 value) external virtual onlyFactory {
        if (version.timestamp == 0) revert KeeperOracleVersionOutsideRangeError();
        PriceResponse memory priceResponse = (version.timestamp == next()) ?
            _commitRequested(version, value) :
            _commitUnrequested(version);
        _global.latestVersion = uint64(version.timestamp);

        emit OracleProviderVersionFulfilled(version);

        try oracle.market() returns (IMarket market) { // v2.3 migration -- don't callback if Oracle is still on its v2.2 implementation
            market.settle(address(0));
            oracle.claimFee(priceResponse.toOracleReceipt(_localCallbacks[version.timestamp].length()).settlementFee);
            market.token().push(receiver, UFixed18Lib.from(priceResponse.syncFee));
        } catch {
            return;
        }
    }

    /// @notice Performs an asynchronous local settlement callback
    /// @dev Distribution of keeper incentive is consolidated in the oracle's factory
    /// @param version The version to settle
    /// @param maxCount The maximum number of settlement callbacks to perform before exiting
    function settle(uint256 version, uint256 maxCount) external virtual onlyFactory {
        EnumerableSet.AddressSet storage callbacks = _localCallbacks[version];

        if (_global.latestVersion < version) revert KeeperOracleVersionOutsideRangeError();
        if (maxCount == 0) revert KeeperOracleInvalidCallbackError();
        if (callbacks.length() == 0) revert KeeperOracleInvalidCallbackError();

        IMarket market = oracle.market();

        for (uint256 i; i < maxCount && callbacks.length() > 0; i++) {
            address account = callbacks.at(0);
            market.settle(account);
            callbacks.remove(account);
            emit CallbackFulfilled(SettlementCallback(market, account, version));

            // full settlement fee already cleamed in commit
            PriceResponse memory priceResponse = _responses[version].read();
            market.token().push(msg.sender, UFixed18Lib.from(priceResponse.asyncFee));
        }
    }

    /// @notice Commits the price to a requested version
    /// @dev This commit function will pay out a keeper fee for providing a valid price or carrying over latest price
    /// @param oracleVersion The oracle version to commit
    /// @param value The value charged to commit the price in ether
    /// @return priceResponse The response to the price request
    function _commitRequested(
        OracleVersion memory oracleVersion,
        uint256 value
    ) private returns (PriceResponse memory priceResponse) {
        IKeeperFactory factory = IKeeperFactory(address(factory()));
        KeeperOracleParameter memory keeperOracleParameter = factory.parameter();

        if (block.timestamp <= (next() + timeout)) {
            if (!oracleVersion.valid) revert KeeperOracleInvalidPriceError();
            priceResponse.price = oracleVersion.price;
            priceResponse.valid = true;
        } else {
            PriceResponse memory latestPrice = _responses[_global.latestVersion].read();
            priceResponse.price = latestPrice.price;
            priceResponse.valid = false;
        }

        priceResponse.syncFee = UFixed6Lib.from(factory.commitmentGasOracle().cost(value), true);
        priceResponse.asyncFee = UFixed6Lib.from(factory.settlementGasOracle().cost(0), true);
        priceResponse.oracleFee = keeperOracleParameter.oracleFee;

        _responses[oracleVersion.timestamp].store(priceResponse);
        _global.latestIndex++;
    }

    /// @notice Commits the price to a non-requested version
    /// @param oracleVersion The oracle version to commit
    /// @return priceResponse The response to the price request
    function _commitUnrequested(OracleVersion memory oracleVersion) private returns (PriceResponse memory priceResponse) {
        if (!oracleVersion.valid) revert KeeperOracleInvalidPriceError();
        if (oracleVersion.timestamp <= _global.latestVersion || (next() != 0 && oracleVersion.timestamp >= next()))
            revert KeeperOracleVersionOutsideRangeError();

        priceResponse = PriceResponseLib.fromUnrequested(oracleVersion);

        _responses[oracleVersion.timestamp].store(priceResponse);
    }

    /// @dev Only allow authorized oracle provider to call
    modifier onlyOracle {
        if (msg.sender != address(oracle)) revert KeeperOracleNotOracleError();
        _;
    }
}","keccak256":"0x5c360436eb13ff32943582a8a8a6b1b3d04e89e61e4819d08bcc443f1fe6c36d","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/keeper/KeeperOracle_Migration.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.24;

import {IMarket, OracleVersion, OracleReceipt } from "../interfaces/IKeeperFactory.sol";
import { PriceResponse } from "./types/PriceResponse.sol";
import { KeeperOracle } from "./KeeperOracle.sol";

/// @title KeeperOracle_Migration
/// @notice Stub implementation to upgrade the prior KeeperOracle to during the v2.3 migration for compatibility.
contract KeeperOracle_Migration is KeeperOracle {
    // sig: 0xd41c17e7
    error NotImplementedError();

    constructor(uint256 timeout_) KeeperOracle(timeout_) { }

    /// @notice Returns an empty response of the correct v2.3 format for forwards compatibility of the previous sub-oracle
    /// @dev Empty responses, as long as they are of the correct format, will be overridden by the Global.latestPrice
    /// @return oracleVersion The empty oracle version
    /// @return oracleReceipt The empty oracle receipt
    function at(uint256 timestamp) public pure override returns (
        OracleVersion memory oracleVersion,
        OracleReceipt memory oracleReceipt
    ) {
        oracleVersion.timestamp = timestamp;
        return (oracleVersion, oracleReceipt);
    }

    /* Do not allow any unintented calls to the previous sub-oracle after migration */

    function localCallbacks(uint256) external pure override returns (address[] memory) { revert NotImplementedError(); }
    function next() public pure override returns (uint256) { revert NotImplementedError(); }
    function responses(uint256) external pure override returns (PriceResponse memory) { revert NotImplementedError(); }
    function request(IMarket, address) external pure override { revert NotImplementedError(); }
    function status() external pure override returns (OracleVersion memory, uint256) { revert NotImplementedError(); }
    function latest() public pure override returns (OracleVersion memory) { revert NotImplementedError(); }
    function current() public pure override returns (uint256) { revert NotImplementedError(); }
    function commit(OracleVersion memory, address, uint256) external pure override { revert NotImplementedError(); }
    function settle(uint256, uint256) external pure override { revert NotImplementedError(); }
}","keccak256":"0xcc5afc4021323706ebf14c6849fb0f49124c67e40d1c42c9168bfe2cd90820fe","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/keeper/types/KeeperOracleParameter.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@equilibria/root/number/types/UFixed6.sol";
import "@equilibria/perennial-v2/contracts/types/OracleVersion.sol";

struct KeeperOracleParameter {
    /// @dev The latest granularity setting in seconds
    uint256 latestGranularity;

    /// @dev The current granularity setting in seconds
    uint256 currentGranularity;

    /// @dev The timestamp at which the current granularity setting becomes effective
    uint256 effectiveAfter;

    /// @dev The relative oracle fee percentage of the request
    UFixed6 oracleFee;

    /// @dev Seconds after version a committed price is valid
    uint256 validFrom;

    /// @dev Seconds after version a committed price is valid until
    uint256 validTo;
}
struct StoredKeeperOracleParameter {
    /* slot 0 (21) */
    uint16 latestGranularity;   // <= 65k
    uint16 currentGranularity;  // <= 65k
    uint32 effectiveAfter;      // <= 2038
    uint24 oracleFee;           // <= 100%
    uint16 validFrom;           // <= 65k
    uint16 validTo;             // <= 65k
}
struct KeeperOracleParameterStorage { StoredKeeperOracleParameter value; }
using KeeperOracleParameterStorageLib for KeeperOracleParameterStorage global;

/// @dev (external-safe): this library is safe to externalize
library KeeperOracleParameterStorageLib {
    // sig: 0xff590172
    error KeeperOracleParameterStorageInvalidError();

    function read(KeeperOracleParameterStorage storage self) internal view returns (KeeperOracleParameter memory) {
        StoredKeeperOracleParameter memory storedValue = self.value;
        return KeeperOracleParameter(
            uint256(storedValue.latestGranularity),
            uint256(storedValue.currentGranularity),
            uint256(storedValue.effectiveAfter),
            UFixed6.wrap(uint256(storedValue.oracleFee)),
            uint256(storedValue.validFrom),
            uint256(storedValue.validTo)
        );
    }

    function validate(KeeperOracleParameter memory newValue) private pure {
        if (newValue.latestGranularity < 1 && newValue.effectiveAfter != 0)
            revert KeeperOracleParameterStorageInvalidError();
        if (newValue.currentGranularity < 1) revert KeeperOracleParameterStorageInvalidError();
    }

    function store(KeeperOracleParameterStorage storage self, KeeperOracleParameter memory newValue) internal {
        validate(newValue);

        if (newValue.latestGranularity > type(uint16).max) revert KeeperOracleParameterStorageInvalidError();
        if (newValue.currentGranularity > type(uint16).max) revert KeeperOracleParameterStorageInvalidError();
        if (newValue.effectiveAfter > type(uint32).max) revert KeeperOracleParameterStorageInvalidError();
        if (newValue.oracleFee.gt(UFixed6.wrap(type(uint24).max))) revert KeeperOracleParameterStorageInvalidError();
        if (newValue.validFrom > type(uint16).max) revert KeeperOracleParameterStorageInvalidError();
        if (newValue.validTo > type(uint16).max) revert KeeperOracleParameterStorageInvalidError();

        self.value = StoredKeeperOracleParameter(
            uint16(newValue.latestGranularity),
            uint16(newValue.currentGranularity),
            uint32(newValue.effectiveAfter),
            uint24(UFixed6.unwrap(newValue.oracleFee)),
            uint16(newValue.validFrom),
            uint16(newValue.validTo)
        );
    }
}
","keccak256":"0xd3c2595fb45514a2b6635bd8e13145741512576f38da25bc605b0df5242f0643","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/keeper/types/PriceResponse.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@equilibria/root/number/types/UFixed6.sol";
import { OracleVersion } from "@equilibria/perennial-v2/contracts/types/OracleVersion.sol";
import { OracleReceipt } from "@equilibria/perennial-v2/contracts/types/OracleReceipt.sol";

struct PriceResponse {
    /// @dev The oracle price of the corresponding version
    Fixed6 price;

    /// @dev the synchronous portion of the fixed settlement fee of the request delivered on commit
    UFixed6 syncFee;

    /// @dev the asynchronous portion of the fixed settlement fee of the request delivered on settlement callback
    UFixed6 asyncFee;

    /// @dev The relative oracle fee percentage of the request
    UFixed6 oracleFee;

    /// @dev Whether the version is valid
    bool valid;
}
using PriceResponseLib for PriceResponse global;
struct StoredPriceResponse {
    /* slot 0 */
    int64 price;            // <= 18t
    uint48 syncFee;         // <= 281m
    uint48 asyncFee;        // <= 281m
    uint24 oracleFee;       // <= 100%
    bool valid;
}
struct PriceResponseStorage { StoredPriceResponse value; }
using PriceResponseStorageLib for PriceResponseStorage global;

/// @title PriceResponseLib
/// @dev (external-unsafe): this library must be used internally only
/// @notice Library for PriceResponse logic and data.
library PriceResponseLib {
    /// @notice Constructs a price response from an unrequested oracle version
    /// @param oracleVersion The oracle version object
    /// @return The corresponding price response
    function fromUnrequested(OracleVersion memory oracleVersion) internal pure returns (PriceResponse memory) {
        return PriceResponse(oracleVersion.price, UFixed6Lib.ZERO, UFixed6Lib.ZERO, UFixed6Lib.ZERO, oracleVersion.valid);
    }

    /// @notice Returns an oracle version based on the price snapshot and timestamp
    /// @param self The price response object
    /// @param timestamp The timestamp of the price snapshot
    /// @return The corresponding oracle version
    function toOracleVersion(PriceResponse memory self, uint256 timestamp) internal pure returns (OracleVersion memory) {
        return OracleVersion(timestamp, self.price, self.valid);
    }

    /// @notice Returns an oracle receipt based on the price snapshot and timestamp
    /// @param self The price response object
    /// @param callbacks The number of settlement callbacks to be made
    /// @return The corresponding oracle receipt
    function toOracleReceipt(PriceResponse memory self, uint256 callbacks) internal pure returns (OracleReceipt memory) {
        return OracleReceipt(self.syncFee.add(self.asyncFee.mul(UFixed6Lib.from(callbacks))), self.oracleFee);
    }
}

/// @dev (external-safe): this library is safe to externalize
library PriceResponseStorageLib {
    // sig: 0xea04171b
    error PriceResponseStorageInvalidError();

    function read(PriceResponseStorage storage self) internal view returns (PriceResponse memory) {
        StoredPriceResponse memory storedValue = self.value;
        return PriceResponse(
            Fixed6.wrap(int256(storedValue.price)),
            UFixed6.wrap(uint256(storedValue.syncFee)),
            UFixed6.wrap(uint256(storedValue.asyncFee)),
            UFixed6.wrap(uint256(storedValue.oracleFee)),
            storedValue.valid
        );
    }

    function store(PriceResponseStorage storage self, PriceResponse memory newValue) internal {
        if (newValue.price.gt(Fixed6.wrap(type(int64).max))) revert PriceResponseStorageInvalidError();
        if (newValue.price.lt(Fixed6.wrap(type(int64).min))) revert PriceResponseStorageInvalidError();
        if (newValue.syncFee.gt(UFixed6.wrap(type(uint48).max))) revert PriceResponseStorageInvalidError();
        if (newValue.asyncFee.gt(UFixed6.wrap(type(uint48).max))) revert PriceResponseStorageInvalidError();
        if (newValue.oracleFee.gt(UFixed6.wrap(type(uint24).max))) revert PriceResponseStorageInvalidError();

        self.value = StoredPriceResponse(
            int64(Fixed6.unwrap(newValue.price)),
            uint48(UFixed6.unwrap(newValue.syncFee)),
            uint48(UFixed6.unwrap(newValue.asyncFee)),
            uint24(UFixed6.unwrap(newValue.oracleFee)),
            newValue.valid
        );
    }
}
","keccak256":"0x176818141f0c581e36eeefda014ecba7111132dfec057e01d8cee9ab684ed142","license":"Apache-2.0"},"@equilibria/perennial-v2-oracle/contracts/types/OracleParameter.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@equilibria/root/number/types/UFixed6.sol";
import "@equilibria/perennial-v2/contracts/types/OracleVersion.sol";

struct OracleParameter {
    /// @dev The cap for the granularity setting in seconds
    uint256 maxGranularity;

    /// @dev the cap for the settle fee in absolute terms
    UFixed6 maxSettlementFee;

    /// @dev The cap for the oracle fee in relative terms
    UFixed6 maxOracleFee;
}
struct StoredOracleParameter {
    /* slot 0 */
    uint16 maxGranularity;      // <= 65k
    uint48 maxSettlementFee;    // <= 281m
    uint24 maxOracleFee;        // <= 100%
}
struct OracleParameterStorage { StoredOracleParameter value; }
using OracleParameterStorageLib for OracleParameterStorage global;

/// @dev (external-safe): this library is safe to externalize
library OracleParameterStorageLib {
    // sig: 0xfc481d85
    error OracleParameterStorageInvalidError();

    function read(OracleParameterStorage storage self) internal view returns (OracleParameter memory) {
        StoredOracleParameter memory storedValue = self.value;
        return OracleParameter(
            uint256(storedValue.maxGranularity),
            UFixed6.wrap(uint256(storedValue.maxSettlementFee)),
            UFixed6.wrap(uint256(storedValue.maxOracleFee))
        );
    }

    function validate(OracleParameter memory newValue) private pure {
        if (newValue.maxGranularity < 1) revert OracleParameterStorageInvalidError();
        if (newValue.maxOracleFee.gt(UFixed6Lib.ONE)) revert OracleParameterStorageInvalidError();
    }

    function store(OracleParameterStorage storage self, OracleParameter memory newValue) internal {
        validate(newValue);

        if (newValue.maxGranularity > type(uint16).max) revert OracleParameterStorageInvalidError();
        if (newValue.maxSettlementFee.gt(UFixed6.wrap(type(uint48).max))) revert OracleParameterStorageInvalidError();
        if (newValue.maxOracleFee.gt(UFixed6.wrap(type(uint24).max))) revert OracleParameterStorageInvalidError();

        self.value = StoredOracleParameter(
            uint16(newValue.maxGranularity),
            uint48(UFixed6.unwrap(newValue.maxSettlementFee)),
            uint24(UFixed6.unwrap(newValue.maxOracleFee))
        );
    }
}
","keccak256":"0xe1ee5d0f515f8b8554d3eb6006b5d74c47927826e455b48eaa1b4219a26c6d0b","license":"Apache-2.0"},"@equilibria/perennial-v2-verifier/contracts/types/Intent.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import { UFixed6, UFixed6Lib } from "@equilibria/root/number/types/UFixed6.sol";
import { Fixed6 } from "@equilibria/root/number/types/Fixed6.sol";
import { Common, CommonLib } from "@equilibria/root/verifier/types/Common.sol";

struct Intent {
    /// @dev The size and direction of the order being opened by the taker
    ///       - Positive opens long / Negative opens short
    ///       - The maker will open the opposite side of the order
    ///       - To close, open an order in the opposite direction
    Fixed6 amount;

    /// @dev The price to execute the order at
    Fixed6 price;

    /// @dev The solver fee, a percentage of the substractive interface fee
    UFixed6 fee;

    /// @dev The referral address of the originator of the order (ex. the interface)
    address originator;

    /// @dev The referral address of the solver of the order (ex. the router)
    address solver;

    /// @dev The minimium collateralization ratio that must be maintained after the order is executed
    UFixed6 collateralization;

    /// @dev The common information for the intent
    Common common;
}
using IntentLib for Intent global;

/// @title IntentLib
/// @notice Library for Intent logic and data.
library IntentLib {
    bytes32 constant public STRUCT_HASH = keccak256(
        "Intent(int256 amount,int256 price,uint256 fee,address originator,address solver,uint256 collateralization,Common common)"
        "Common(address account,address signer,address domain,uint256 nonce,uint256 group,uint256 expiry)"
    );

    function hash(Intent memory self) internal pure returns (bytes32) {
        return keccak256(abi.encode(STRUCT_HASH, self.amount, self.price, self.fee, self.originator, self.solver, self.collateralization, CommonLib.hash(self.common)));
    }
}
