pragma solidity ^0.8.13;

import "@equilibria/root/pid/types/PAccumulator6.sol";
import "./ProtocolParameter.sol";
import "./MarketParameter.sol";
import "../libs/VersionLib.sol";

/// @dev Global type
struct Global {
    /// @dev The current position ID
    uint256 currentId;

    /// @dev The latest position id
    uint256 latestId;

    /// @dev The accrued protocol fee
    UFixed6 protocolFee;

    /// @dev The accrued oracle fee
    UFixed6 oracleFee;

    /// @dev The accrued risk fee
    UFixed6 riskFee;

    /// @dev The latest valid price in the market
    Fixed6 latestPrice;

    /// @dev The accumulated market exposure
    Fixed6 exposure;

    /// @dev The current PAccumulator state
    PAccumulator6 pAccumulator;
}
using GlobalLib for Global global;
struct GlobalStorage { uint256 slot0; uint256 slot1; } // SECURITY: must remain at (2) slots
using GlobalStorageLib for GlobalStorage global;

/// @title Global
/// @dev (external-unsafe): this library must be used internally only
/// @notice Holds the global market state
library GlobalLib {
    /// @notice Updates market exposure based on a change in the risk parameter configuration
    /// @param self The Global object to update
    /// @param latestRiskParameter The latest risk parameter configuration
    /// @param newRiskParameter The new risk parameter configuration
    /// @param latestPosition The latest position
    function update(
        Global memory self,
        RiskParameter memory latestRiskParameter,
        RiskParameter memory newRiskParameter,
        Position memory latestPosition
    ) internal pure {
        Fixed6 exposureChange = latestRiskParameter.takerFee
            .exposure(newRiskParameter.takerFee, latestPosition.skew(), self.latestPrice.abs());
        self.exposure = self.exposure.sub(exposureChange);
    }

    /// @notice Increments the fees by `amount` using current parameters
    /// @dev Computes the fees based on the current market parameters
    ///      market fee -> trade fee + market's trade offset + funding fee + interest fee
    ///        1. oracle fee taken out as a percentage of what's left of market fee
    ///        2. risk fee taken out as a percentage of what's left of market fee
    ///        3. protocol fee is what's left of market fee
    /// @param self The Global object to update
    /// @param newLatestId The new latest position id
    /// @param accumulation The accumulation result
    /// @param marketParameter The current market parameters
    /// @param oracleReceipt The receipt of the corresponding oracle version
    function update(
        Global memory self,
        uint256 newLatestId,
        VersionAccumulationResponse memory accumulation,
        MarketParameter memory marketParameter,
        OracleReceipt memory oracleReceipt
    ) internal pure {
        UFixed6 marketFee = accumulation.marketFee;

        UFixed6 oracleFee = marketFee.mul(oracleReceipt.oracleFee);
        marketFee = marketFee.sub(oracleFee);

        UFixed6 riskFee = marketFee.mul(marketParameter.riskFee);
        marketFee = marketFee.sub(riskFee);

        self.latestId = newLatestId;
        self.protocolFee = self.protocolFee.add(marketFee);
        self.oracleFee = self.oracleFee.add(accumulation.settlementFee).add(oracleFee);
        self.riskFee = self.riskFee.add(riskFee);
        self.exposure = self.exposure.add(accumulation.marketExposure);
    }

    /// @notice Overrides the price of the oracle with the latest global version if it is empty
    /// @param self The Global object to read from
    /// @param oracleVersion The oracle version to update
    function overrideIfZero(Global memory self, OracleVersion memory oracleVersion) internal pure {
        if (oracleVersion.price.isZero()) oracleVersion.price = self.latestPrice;
    }
}

/// @dev Manually encodes and decodes the Global struct into storage.
///      (external-safe): this library is safe to externalize
///
///     struct StoredGlobal {
///         /* slot 0 */
///         uint32 currentId;           // <= 4.29b
///         uint32 latestId;            // <= 4.29b
///         uint48 protocolFee;         // <= 281m
///         uint48 oracleFee;           // <= 281m
///         uint48 riskFee;             // <= 281m
///
///         /* slot 1 */
///         int32 pAccumulator.value;   // <= 214000%
///         int24 pAccumulator.skew;    // <= 838%
///         int64 latestPrice;          // <= 9.22t
///         int64 exposure;             // <= 9.22t
///     }
///
library GlobalStorageLib {
    // sig: 0x2142bc27
    error GlobalStorageInvalidError();

    function read(GlobalStorage storage self) internal view returns (Global memory) {
        (uint256 slot0, uint256 slot1) = (self.slot0, self.slot1);
        return Global(
            uint256(slot0 << (256 - 32)) >> (256 - 32),
            uint256(slot0 << (256 - 32 - 32)) >> (256 - 32),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 32 - 48)) >> (256 - 48)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 32 - 48 - 48)) >> (256 - 48)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 32 - 48 - 48 - 48)) >> (256 - 48)),
            Fixed6.wrap(int256(slot1 << (256 - 32 - 24 - 64)) >> (256 - 64)),
            Fixed6.wrap(int256(slot1 << (256 - 32 - 24 - 64 - 64)) >> (256 - 64)),
            PAccumulator6(
                Fixed6.wrap(int256(slot1 << (256 - 32)) >> (256 - 32)),
                Fixed6.wrap(int256(slot1 << (256 - 32 - 24)) >> (256 - 24))
            )
        );
    }

    function store(GlobalStorage storage self, Global memory newValue) external {
        if (newValue.currentId > uint256(type(uint32).max)) revert GlobalStorageInvalidError();
        if (newValue.latestId > uint256(type(uint32).max)) revert GlobalStorageInvalidError();
        if (newValue.protocolFee.gt(UFixed6.wrap(type(uint48).max))) revert GlobalStorageInvalidError();
        if (newValue.oracleFee.gt(UFixed6.wrap(type(uint48).max))) revert GlobalStorageInvalidError();
        if (newValue.riskFee.gt(UFixed6.wrap(type(uint48).max))) revert GlobalStorageInvalidError();
        if (newValue.latestPrice.gt(Fixed6.wrap(type(int64).max))) revert GlobalStorageInvalidError();
        if (newValue.latestPrice.lt(Fixed6.wrap(type(int64).min))) revert GlobalStorageInvalidError();
        if (newValue.exposure.gt(Fixed6.wrap(type(int64).max))) revert GlobalStorageInvalidError();
        if (newValue.exposure.lt(Fixed6.wrap(type(int64).min))) revert GlobalStorageInvalidError();
        if (newValue.pAccumulator._value.gt(Fixed6.wrap(type(int32).max))) revert GlobalStorageInvalidError();
        if (newValue.pAccumulator._value.lt(Fixed6.wrap(type(int32).min))) revert GlobalStorageInvalidError();
        if (newValue.pAccumulator._skew.gt(Fixed6.wrap(type(int24).max))) revert GlobalStorageInvalidError();
        if (newValue.pAccumulator._skew.lt(Fixed6.wrap(type(int24).min))) revert GlobalStorageInvalidError();

        uint256 encoded0 =
            uint256(newValue.currentId << (256 - 32)) >> (256 - 32) |
            uint256(newValue.latestId << (256 - 32)) >> (256 - 32 - 32) |
            uint256(UFixed6.unwrap(newValue.protocolFee) << (256 - 48)) >> (256 - 32 - 32 - 48) |
            uint256(UFixed6.unwrap(newValue.oracleFee) << (256 - 48)) >> (256 - 32 - 32 - 48 - 48) |
            uint256(UFixed6.unwrap(newValue.riskFee) << (256 - 48)) >> (256 - 32 - 32 - 48 - 48 - 48);

        uint256 encoded1 =
            uint256(Fixed6.unwrap(newValue.pAccumulator._value) << (256 - 32)) >> (256 - 32) |
            uint256(Fixed6.unwrap(newValue.pAccumulator._skew) << (256 - 24)) >> (256 - 32 - 24) |
            uint256(Fixed6.unwrap(newValue.latestPrice) << (256 - 64)) >> (256 - 32 - 24 - 64) |
            uint256(Fixed6.unwrap(newValue.exposure) << (256 - 64)) >> (256 - 32 - 24 - 64 - 64);

        assembly {
            sstore(self.slot, encoded0)
            sstore(add(self.slot, 1), encoded1)
        }
    }
}
","keccak256":"0x44686dc21f3a15b1fd1ea0437a893db6501a89508f9a74302707f8c9dcf8ec1f","license":"Apache-2.0"},"@equilibria/perennial-v2/contracts/types/Guarantee.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "./Order.sol";

/// @dev Guarantee type
struct Guarantee {
    /// @dev The quantity of guarantees that that will be exempt from the settlement fee
    uint256 orders;

    /// @dev The notional of the magnitude with the price override (local only)
    Fixed6 notional;

    /// @dev The positive skew (open long / close short) guarantee size
    UFixed6 takerPos;

    /// @dev The negative skew (close long / open short) guarantee size
    UFixed6 takerNeg;

    /// @dev The magnitude of the guarantee that be exempt from the trade fee
    UFixed6 takerFee;

    /// @dev The referral fee multiplied by the size applicable to the referral (local only)
    UFixed6 referral;
}
using GuaranteeLib for Guarantee global;
struct GuaranteeStorageGlobal { uint256 slot0; uint256 slot1; } // SECURITY: must remain at (2) slots
using GuaranteeStorageGlobalLib for GuaranteeStorageGlobal global;
struct GuaranteeStorageLocal { uint256 slot0; uint256 slot1; } // SECURITY: must remain at (2) slots
using GuaranteeStorageLocalLib for GuaranteeStorageLocal global;

/// @title Guarantee
/// @dev (external-unsafe): this library must be used internally only
/// @notice Holds the state for an account's update guarantee
library GuaranteeLib {
    /// @notice Prepares the next guarantee from the current guarantee
    /// @param self The guarantee object to update
    function next(Guarantee memory self) internal pure  {
        invalidate(self);
        self.orders = 0;
    }

    /// @notice Invalidates the guarantee
    /// @param self The guarantee object to update
    function invalidate(Guarantee memory self) internal pure {
        (self.takerPos, self.takerNeg, self.notional, self.takerFee, self.referral) =
            (UFixed6Lib.ZERO, UFixed6Lib.ZERO, Fixed6Lib.ZERO, UFixed6Lib.ZERO, UFixed6Lib.ZERO);
    }

    /// @notice Creates a new guarantee from an order
    /// @param order The order to create the guarantee from
    /// @param priceOverride The price override
    /// @param referralFee The the percentage of the subtractive fee to take as a solver referral fee
    /// @param chargeSettlementFee Whether the order will still be charged the settlement fee
    /// @param chargeTradeFee Whether the order will still be charged the trade fee
    /// @return newGuarantee The resulting guarantee
    function from(
        Order memory order,
        Fixed6 priceOverride,
        UFixed6 referralFee,
        bool chargeSettlementFee,
        bool chargeTradeFee
    ) internal pure returns (Guarantee memory newGuarantee) {
        // maker orders and one intent order per fill will be required to pay the settlement fee
        if (!order.takerTotal().isZero() && !chargeSettlementFee) newGuarantee.orders = order.orders;

        (newGuarantee.takerPos, newGuarantee.takerNeg) =
            (order.longPos.add(order.shortNeg), order.longNeg.add(order.shortPos));
        newGuarantee.takerFee = chargeTradeFee ? UFixed6Lib.ZERO : order.takerTotal();

        newGuarantee.notional = taker(newGuarantee).mul(priceOverride);
        newGuarantee.referral = order.takerReferral.mul(referralFee);
    }

    /// @notice Returns the taker delta of the guarantee
    /// @param self The guarantee object to check
    /// @return The taker delta of the guarantee
    function taker(Guarantee memory self) internal pure returns (Fixed6) {
        return Fixed6Lib.from(self.takerPos).sub(Fixed6Lib.from(self.takerNeg));
    }

    /// @notice Returns the total taker delta of the guarantee
    /// @param self The guarantee object to check
    /// @return The total taker delta of the guarantee
    function takerTotal(Guarantee memory self) internal pure returns (UFixed6) {
        return self.takerPos.add(self.takerNeg);
    }

    /// @notice Updates the current global guarantee with a new local guarantee
    /// @param self The guarantee object to update
    /// @param guarantee The new guarantee
    function add(Guarantee memory self, Guarantee memory guarantee) internal pure {
        self.orders = self.orders + guarantee.orders;
        (self.notional, self.takerPos, self.takerNeg, self.takerFee, self.referral) = (
            self.notional.add(guarantee.notional),
            self.takerPos.add(guarantee.takerPos),
            self.takerNeg.add(guarantee.takerNeg),
            self.takerFee.add(guarantee.takerFee),
            self.referral.add(guarantee.referral)
        );
    }
}

/// @dev Manually encodes and decodes the global Guarantee struct into storage.
///      (external-safe): this library is safe to externalize
///
///     struct StoredGuaranteeGlobal {
///         /* slot 0 */
///         uint32 orders;
///         uint64 takerPos;
///         uint64 takerNeg;
///         uint64 takerFee;
///     }
///
library GuaranteeStorageGlobalLib {
    function read(GuaranteeStorageGlobal storage self) internal view returns (Guarantee memory) {
        uint256 slot0 = self.slot0;
        return Guarantee(
            uint256(slot0 << (256 - 32)) >> (256 - 32),
            Fixed6Lib.ZERO,
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 64 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 64 - 64 - 64)) >> (256 - 64)),
            UFixed6Lib.ZERO
        );
    }

    function store(GuaranteeStorageGlobal storage self, Guarantee memory newValue) internal {
        GuaranteeStorageLib.validate(newValue);

        uint256 encoded0 =
            uint256(newValue.orders << (256 - 32)) >> (256 - 32) |
            uint256(UFixed6.unwrap(newValue.takerPos) << (256 - 64)) >> (256 - 32 - 64) |
            uint256(UFixed6.unwrap(newValue.takerNeg) << (256 - 64)) >> (256 - 32 - 64 - 64) |
            uint256(UFixed6.unwrap(newValue.takerFee) << (256 - 64)) >> (256 - 32 - 64 - 64 - 64);

        assembly {
            sstore(self.slot, encoded0)
        }
    }
}

/// @dev Manually encodes and decodes the local Guarantee struct into storage.
///      (external-safe): this library is safe to externalize
///
///     struct StoredGuaranteeLocal {
///         /* slot 0 */
///         uint32 orders;
///         int64 notional;
///         uint64 takerPos;
///         uint64 takerNeg;
///
///         /* slot 1 */
///         uint64 takerFee;
///         uint64 referral;
///     }
///
library GuaranteeStorageLocalLib {
    function read(GuaranteeStorageLocal storage self) internal view returns (Guarantee memory) {
        (uint256 slot0, uint256 slot1) = (self.slot0, self.slot1);
        return Guarantee(
            uint256(slot0 << (256 - 32)) >> (256 - 32),
            Fixed6.wrap(int256(slot0 << (256 - 32 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 64 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot0 << (256 - 32 - 64 - 64 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot1 << (256 - 64)) >> (256 - 64)),
            UFixed6.wrap(uint256(slot1 << (256 - 64 - 64)) >> (256 - 64))
        );
    }

    function store(GuaranteeStorageLocal storage self, Guarantee memory newValue) internal {
        GuaranteeStorageLib.validate(newValue);

        if (newValue.notional.gt(Fixed6.wrap(type(int64).max))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();
        if (newValue.notional.lt(Fixed6.wrap(type(int64).min))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();
        if (newValue.referral.gt(UFixed6.wrap(type(uint64).max))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();

        uint256 encoded0 =
            uint256(newValue.orders << (256 - 32)) >> (256 - 32) |
            uint256(Fixed6.unwrap(newValue.notional) << (256 - 64)) >> (256 - 32 - 64) |
            uint256(UFixed6.unwrap(newValue.takerPos) << (256 - 64)) >> (256 - 32 - 64 - 64) |
            uint256(UFixed6.unwrap(newValue.takerNeg) << (256 - 64)) >> (256 - 32 - 64 - 64 - 64);
        uint256 encode1 =
            uint256(UFixed6.unwrap(newValue.takerFee) << (256 - 64)) >> (256 - 64) |
            uint256(UFixed6.unwrap(newValue.referral) << (256 - 64)) >> (256 - 64 - 64);

        assembly {
            sstore(self.slot, encoded0)
            sstore(add(self.slot, 1), encode1)
        }
    }
}

library GuaranteeStorageLib {
    // sig: 0xfd030f36
    error GuaranteeStorageInvalidError();

    function validate(Guarantee memory newValue) internal pure {
        if (newValue.orders > type(uint32).max) revert GuaranteeStorageInvalidError();
        if (newValue.takerPos.gt(UFixed6.wrap(type(uint64).max))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();
        if (newValue.takerNeg.gt(UFixed6.wrap(type(uint64).max))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();
        if (newValue.takerFee.gt(UFixed6.wrap(type(uint64).max))) revert GuaranteeStorageLib.GuaranteeStorageInvalidError();
    }
